import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from './../services/auth.service';

@Component({
  selector: 'app-change-password',
  templateUrl: './change-password.component.html',
  styleUrls: ['./change-password.component.css']
})
export class ChangePasswordComponent {

  public username: string = '';
  public currentPassword: string = '';
  public newPassword: string = '';
  public confirmNewPassword: string = '';
  public errorMessage: string = '';

  constructor(private router: Router, private authService: AuthService) {}

  changePassword(): void {

    this.onSignIn(this.username, this.currentPassword, this.newPassword, this.confirmNewPassword);

    const success = false;

    this.authService.changePassword(this.username.trim(), this.currentPassword.trim(), this.newPassword.trim(), this.confirmNewPassword.trim()).subscribe({
      next: response => {
        if (response && response.accessToken) {
          // Store the token in local storage
          localStorage.setItem('current_user', JSON.stringify(response));
          localStorage.setItem('access_token', response.accessToken);
          // Navigate to the dashboard
          this.router.navigate(['/employees']);
        } else
          this.errorMessage = 'Invalid username or password';
      }, error: error => {
        console.log(error);
        this.errorMessage = 'Invalid username or password';
      },
      complete: () => console.info('complete')
    });
  }

  home(): void {
    // Redirect to the login page after logout
    this.router.navigate(['/employees']);
  }

  logout(): void {
    // Clear the access token from localStorage or perform any other logout actions
    localStorage.removeItem('access_token');

    // Redirect to the login page after logout
    this.router.navigate(['/login']);
  }

}
